import React from 'react';
import axios from 'axios';
class Yorders extends React.Component{
	constructor(props){
		super(props);
		this.state={
			transaction:[]
		}
	}
	componentDidMount(){
		axios({
			method: 'GET',
			url: 'http://localhost:4000/order/getall',
			withCredentials:true
		  }).then((response) => {
			  this.setState({transaction:response.data});
		  }, (error) => {
			
		  });
	}
	render(){
		return (
			<div className='row'>
				<div className='col align-self-center'>
					{this.state.transaction.map((t)=>{
						return(
							<div class="card text-dark bg-light mb-3" style="max-width: 18rem;">
	  						<div class="card-header">{t._id}</div>
  								<div class="card-body">
									<h5 class="card-title">{t.date}</h5>
    								<p class="card-text">{t.products.length}</p>
  								</div>
							</div>
						)
					})}
				</div>
			</div>
		);
	}
}

export default Yorders;
