import React,{useState,useEffect} from 'react';
import Sidefilter from './sidefilter';
import axios from 'axios';
import Singleproductlist from './single_product_list';
function Product({ match }){
	const [products,setproducts]=useState('');
	useEffect(() => {
		const fetchproducts = () =>{
			axios({
				method: 'GET',
				url: 'http://localhost:4000/product/'+match.params.type,
			}).then((response) => {
				const final_data=response.data;
				setproducts(final_data);
			  }, (error) => {
					//alert(error);
			  });
		}
		fetchproducts();
	},[setproducts,match.params.type]);
	return (
		<div className='container'>
			<div className='row justify-contents-center'>
				<div className='col-2'>
					<button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#sortnfilter">Filter/Sort</button>
					<div className="modal fade" id='sortnfilter' tabIndex="-1" aria-labelledby="sortnfilter" aria-hidden="true">
						<div className='modal-body modal-fullscreen-sm-down'>
							<div className='modal-body'>
								<Sidefilter/>
							</div>
						</div>
					</div>
				</div>
			</div>
			<div className='row justify-contents-start'>
				<div className='col-10'>
					<Singleproductlist data={products}/>	
				</div>
			</div>
		</div>
	);
}
export default Product;
