import React from 'react';
import Showprescription from './showprescription';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Loadingspinner from './Loadingspinner';
class Cart extends React.Component{
	constructor(props){
		super(props);
		this.state={
			cart:[],
			fetched:'loading',
			total_price:0
		}
		this.handleOnClick=this.handleOnClick.bind(this);
	}
	handleOnClick(e){
		axios({
			method: 'DELETE',
			url: 'http://localhost:5000/cart/delete',
			withCredentials: true,
			data:{
				'pid':e.target.value
			}
		  }).then((response) => {
				this.setState({cart:response.data[0]['cart']});
		  }).catch((error) => {
				console.log(error);
		  });
	}
	componentDidMount(){
		const fetchcart = () => {
			axios({
				method: 'GET',
				url: 'http://localhost:5000/cart/getcart',
				withCredentials: true,
			  }).then((response) => {
					this.setState({fetched:true});
					this.setState({cart:response.data[0]['cart']});
			  }).catch((error) => {
					this.setState({fetched:true});
					if(error.response!==undefined){
						if(error.response.status===403)
							window.location='http://localhost:3000/login';
					}
			  });
	   };
		fetchcart();
	}
	render(){
		if(this.state.fetched === 'loading'){
			return(<Loadingspinner/>);
		}else{
			if(this.state.cart.length!==0){
				return (
					<div className='table-responsive'>
						<h1 className="display-6">Cart</h1>
						<table className='table table-striped'>
							<thead>
								<tr>
									<th>
										Product name
									</th>
									<th>
										Quantity
									</th>
									<th>
										Price
									</th>
									<th>
										Lens Details
									</th>
									<th>
										Total Price
									</th>
								</tr>
							</thead>
							<tbody>
								{this.state.cart.map((e) => (
									<tr>
										<td>
											<Link to={"/productdisplay/"+e._id}>{e.productname}</Link>
										</td>
										<td>
											{e.quantity}
										</td>
										<td>
											{e.price}
										</td>
										<td>
											<Showprescription lens={e.lens_details}/>
										</td>
										<td>
											{parseInt(e.price)*e.quantity}
										</td>
										<td>
											<button className='btn btn-danger' value={e._id} onClick={this.handleOnClick} type='submit' id="delete">Delete</button>
										</td>
									</tr>
								))}
								<tr>
									<td colSpan='4'>
										Total Price =
									</td>
									<td colSpan='2'>
										{this.state.total_price}
									</td>
								</tr>
							</tbody>
						</table>
					</div>
				);
			}else{
				return(
					<div style={{'height':'100vh'}}className="border d-flex align-items-center justify-content-center">
						<h1>Oops no items in cart!!!!</h1>
    	    		</div>
				);
			}
		}
	};
}
export default Cart;
