import React from 'react';
import {Link} from 'react-router-dom';
function single_product_list(props){
    const {data}=props;
    console.log(data);
    return(
        <div>
            {data.map(({_id,productname,productprice,image_path,productdescription,brand})=>(
            <div>
                <div style={{'width':'40%'}}>
                    <img src={image_path}/>
                </div>
                <div style={{'width':'60%'}}>
                    <div style={{'width':'100%'}}>
                        <Link to={"/productdisplay/"+_id} key={_id}><h4>{productname}</h4></Link>
                    </div>
                    <div style={{'width':'100%'}}>
                        <p> {productdescription}</p>
                    </div>
                    <div style={{'width':'100%'}}>
                        {brand}
                    </div><br/>
                    <div style={{'width':'100%'}}>
                        {productprice}
                    </div>
                </div>
            </div>
        ))};
        </div>
    );
}
export default single_product_list;
