import React from 'react';
import axios from 'axios';
import Enterprescription from './enterprescription';
class ProductDisplay extends React.Component{
	constructor(props){
		super(props);
		this.state={
			id:'',
			lens_details:{
				rsign:'0',
				lsign:'0',
				resph:0,
				reax:0,
				recyl:0,
				readd:0,
				lesph:0,
				leax:0,
				lecyl:0,
				leadd:0,
			},
			product:[],
			shopname:[]
		}
		this.handleChange=this.handleChange.bind(this);
		this.handleSubmit=this.handleSubmit.bind(this);
	}
	handleSubmit(event){
		axios({
			method: 'POST',
			url: 'http://localhost:5000/cart/addtocart',
			withCredentials: true,
			data:{
				'id':this.props.match.params.productId,
				'lens_details':this.state.lens_details
			}
		}).then((response) => {
		}).catch((error) => {
			alert(error);
		});
	}
	handleChange(event){
		const { name, value } = event.target;
		this.setState(prevState => ({
			lens_details: { ...prevState.lens_details, [name]: value }
		}));
	}
	componentDidMount(){
		const fetchproduct = () => {
			axios({
				method: 'GET',
				url: 'http://localhost:5000/product/getsingle?id='+this.props.match.params.productId,
				withCredentials: true,
			  }).then((response) => {
				this.setState({product:response.data});
				this.setState({shopname:response.data[0]['shop']});
			  }, (error) => {
					alert(error);
			  });
	   };
		fetchproduct();
	}
	render(){
		return (
			<div className='container'>
				{this.state.product.length!==0 && this.state.shopname.length!==0?
				<div className='row justify-contents-start'>
					<div className='col-sm'>
						<img src={this.state.product[0]['path']} alt={this.state.product[0]['productname']}/>
					</div>
					<div className='col-sm'>
						<div>
							<h4>{this.state.product[0]['productname']}</h4>
						</div>	
						<div>
							<h6>Brand: {this.state.product[0]['brand']}</h6>
						</div>
						<div>
							<h6>Sold by: {this.state.shopname[0]['shopname']}</h6>
						</div>

						<div>
							<h6>Price: {this.state.product[0]['price']}</h6>
						</div>	
						<div>
							<h6>{this.state.product[0]['productdescription']}</h6>
						</div>
						<Enterprescription handleChange={this.handleChange} lens={this.state.lens_details} handleSubmit={this.handleSubmit}/>	
					</div>
				</div>:null}
			</div>
		);
	}
}

export default ProductDisplay;
