import React from 'react';
import Showprescription from './showprescription';
import axios from 'axios';
class Cart extends React.Component{
	constructor(props){
		super(props);
		this.state={
			cart:[],
			lens_details:[]
		}
	}
	componentDidMount(){
		const fetchcart = () => {
			axios({
				method: 'GET',
				url: 'http://localhost:4000/cart/getcart',
				withCredentials: true,
			  }).then((response) => {
				  console.log(response.data[0]['cart']);
				this.setState({cart:response.data[0]['cart']});
			  }, (error) => {
					alert(error);
			  });
	   };
		fetchcart();
	}
	render(){
		return (
			<div>
				{console.log(this.state.cart.length)}
				{this.state.cart.length !==0 ?
					this.state.cart.map((e) => {
					<div>
						<div style={{'width':'30%'}}>
							{e.productname}
						</div>
						<div style={{'width':'15%'}}>
							{e.quantity}
						</div>
						<div style={{'width':'15%'}}>
							{e.productprice}
						</div>
						<div style={{'width':'20%'}}>
							{parseInt(e.productprice)*e.quantity}
						</div>
						<div style={{'width':'20%'}}>
							<Showprescription lens={e.lens_details}/>	
						</div>
					</div>
				}):null}
				
			</div>
		)
	};
}
export default Cart;
