import React from 'react';

class Register_seller extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
            email: '',
            password:'',
            confirm_password:'',
            type_of_user:'',
            name:'',
            owner:'',
            username:'',
            phone:'',
            company_registration:'',
            password_equal:'',
            unamavailable:''
        };
        this.fileInput = React.createRef();
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this);
        this.handleClick=this.handleClick.bind(this);
    }
    handleClick(e){
        const username=e.target.value;
        const component=this;
        axios({
            method: 'get',
            url: 'http://localhost:5000/seller/availability/'+username,
            config:{ headers: {'Content-Type':'multipart/form-data'}},
            withCredentials:true,
            data: formData
        }).then(function (response) {
            component.setState({unamavailable:response.data});
        }).catch(function (err) {
            console.log(err);
        });
    }
    fileInput(e){
        this.setState({ company_registration:e.target.files[0] });
    }
    handleChange(event) {
        if(e.target.name==='password'){
            let regex=RegExp("^(((?=.*[a-z])(?=.*[A-Z]))|((?=.*[a-z])(?=.*[0-9]))|((?=.*[A-Z])(?=.*[0-9])))(?=.{6,})");
            if(regex.test(e.target.value)===true){
                this.setState({password:e.target.value});
                return;
            }
        }
        if(e.target.name==='confirm_password'){
            if(e.target.value===this.state.password){
                this.setState({password_equal:true});
            }else{
                this.setState({password_equal:false});
            }
        }
        const name = target.name;
        this.setState({[name]: event.target.value});
    }
    handleSubmit(event) {
        event.preventDefault();
        if(this.state.unamavailable===true && this.state.password_equal===true){
            const formData = new FormData(); 
            formData.append('email',this.state.email);
            formData.append('password',this.state.password);
            formData.append('name',this.state.name);
            formData.append('owner',this.state.owner);
            formData.append('username',this.state.username);
            formData.append('phone',this.state.phone);
            formData.append( "company_registration", this.state.company_registration);
            axios({
                method: 'POST',
                url: 'http://localhost:5000/product/create',
                config:{ headers: {'Content-Type':'multipart/form-data'}},
                data: formData
            }).then(function (response) {
                window.location='htpp://localhost:3000/home';
            }).catch(function (err) {
                console.log(err);
            });
        }
    }

    render(){
        return (
            <div>
    	        <div >
    		    	<div>
    			    	<div className='row'>
                            <div className='col align-self-center'>
    					    	<h2>New Seller Signup!</h2>
    						    <form  onSubmit={this.handleSubmit}><br/>
    				    			<input className='form-control' type="text" placeholder="Username" name="username" required />&nbsp; <button className='btn btn-primary' onClick={this.handleClick}>Check availability</button><br/>
	        		    			{this.state.unamavailable===true ? <div class="alert alert-danger" role="alert">Username already in use!!</div>:this.state.unamavailable===false?<div class="alert alert-success" role="alert">Username available!</div> :null}
                                    <input className='form-control' placeholder="Email Address" type="email" name="email" required /><br/>
		            				<input className='form-control' name="phone" placeholder="Phone Number" type="tel" pattern="[0-9]{10}" required/><br/>
	    	        				<label className='form-label' for='companyCertificate'>Company Registration Certificate</label><input id='companyCertificate' className='form-control' type="File" name="company_registration" required accept="image/*" ref={this.fileInput}/><br/>
    			        			<input className='form-control' type="text" placeholder="Seller Name" name="name" required /><br/>
					    		    <input className='form-control' type="text" placeholder="Owner Name" name="owner" required /><br/>
					        	    <input className='form-control' type="password" placeholder="Password" name="password" required id="pass" onkeyup={this.checkpass}/><i id="wrong" style={{'display': 'none'}}>Password Do not match..</i><br/>
            					    <input className='form-control' name="confirm_password" placeholder="Confirm Password" required onkeyup={this.checkpass} id="confirm" />
                                    {this.state.password_equal===true ? <div class="alert alert-danger" role="alert">Username already in use!!</div>:this.state.password_equal===false?<div class="alert alert-success" role="alert">Username available!</div> :null}
                                    <br/>
	    						    <button type='submit' variant="btn btn-primary">Signup</button>
    	        				</form>
			        		</div>
    			        </div>
        		    </div>
            	</div>
            </div>
        );
        }
}

export default Register_seller;
