import axios from 'axios';
import React from 'react';
import Loadingspinner from './Loadingspinner';
import Showprescription from './showprescription';
export default class Recievedorders extends React.Component{
	constructor(props){
		super(props);
		this.state={
			orders:[],
			fetched:'loading',
			awb:'',
			delivery_partner:''
		}
		this.handleOnChange=this.handleOnChange.bind(this);
	}
	handleOnChange(e){
		const name=e.target.name;
		this.setState({[name]:e.target.value});
	}
	componentDidMount(){
		axios({
			url:'http://localhost:5000/order/getall',
			withCredentials:true
		}).then((response)=>{
			this.setState({orders:response.data,fetched:true});
		}).catch((error)=>{
			this.setState({fetched:true});
			console.log(error);
		})
	}
	
	render(){
		if(this.state.fetched==='loading'){
			return (<Loadingspinner/>);	
		}else{
			return (
				<div className='table-responsive'>
					<h1 className="display-6">Recievedorders</h1>
					<table className='table table-striped'>
						<thead>
							<tr>
								<th>Product Name</th>
								<th>Lens Details</th>
								<th>Client Name</th>
								<th>Quantity</th>
								<th>delivery Address</th>
								<th>AWB NO</th>
								<th>Delivery Partner</th>
								<th>&nbsp;</th>
							</tr>
						</thead>
						<tbody>
							{this.state.orders.length !==0 ?this.state.orders.map((order)=>{
								return(
									<tr>
										<td>{order['products']['productname']}</td>
										<td><Showprescription lens={order['products']['lens_details']}/></td>
										<td>{order['clientname']}</td>
										<td>{order['products']['quantity']}</td>
										<td>{order['deliveryaddress']}</td>
										<td><input type='text' placeholder='AWB No.' className='form-control' name='awb' value={this.state.awb} onChange={this.handleOnChange}/></td>
										<td><input type='text' placeholder='Delivery partner' className='form-control' name='delivery_partner' value={this.state.delivery_partner} onChange={this.handleOnChange}/></td>
										<td><button type='submit' className='btn btn-primary' value={order['_id']}>Submit</button></td>
									</tr>
								);
							}):null}
						</tbody>
					</table>
				</div>
			);
		}
	}
}
