import React from 'react';
import { Link } from 'react-router-dom';
import Sidefilter from './sidefilter';
import axios from 'axios';
function Product({ match }){
	const [products,setproducts]=useState();
	let type=match.params.type;
	axios({
		method: 'POST',
		url: 'http://localhost:4000/product/'+type,
	}).then((response) => {
		setproducts(response);
  	}, (error) => {
			alert(error);
  	});
	return (
		<div style={{'width':'100%','display':'flex'}}>
			<div style={{'width':'20%','height':'100vh','display':'flex'}}>
				<Sidefilter/>
			</div>
			<div style={{'width':'75%','height':'100vh','marginLeft':'5%'}}>
				{products.map(({productid,productname,productprice,sellername,image_path})=>(
					<div>
						<div style={{'width':'40%'}}>
							<img src={image_path}/>
						</div>
						<div style={{'width':'60%'}}>
							<div style={{'width':'100%'}}>
								<Link to={"/productdisplay/"+productid} key={productid}><h4>{productname}</h4></Link>
							</div>
							<div style={{'width':'100%'}}>
								{sellername}
							</div><br/>
							<div style={{'width':'100%'}}>
								{productprice}
							</div>
						</div>
					</div>
				))}
			</div>
		</div>
	);
}

export default Product;
