import React, { useEffect, useState } from 'react';
import axios from 'axios';
function ProductDisplay({ match }){
	const[product,setproduct]=useState('');
	useEffect(() => {
		axios({
			method: 'GET',
			url: 'http://localhost:4000/product/getsingle?id='+match.params.productId,
			withCredentials: true,
		  }).then((response) => {
			const final_data=response.data;
			setproduct(final_data);
			console.log(product);
		  }, (error) => {
				alert(error);
		  });	
	},[]);
	
	return (
		<div>
			{product.map((p)=>
			<div>
				<div style={{'width':'30%'}}></div>
				<div style={{'width':'70%'}}>
					<div style={{'width':'100%'}}>
						<h4>{p['productname']}</h4>
					</div>
					<div style={{'width':'100%'}}>
						<h6>Sold by: {p['shop']['shopname']}</h6>
					</div>	
					<div style={{'width':'100%'}}>
						<h6>Brand: {p['brand']}</h6>
					</div>	
					<div style={{'width':'100%'}}>
						<h6>Price: {p['productprice']}</h6>
					</div>	
				</div>
			</div>
			)}
		</div>
	);
}

export default ProductDisplay;
