import React, { useEffect, useState } from 'react';
import {Navbar,Nav} from 'react-bootstrap';
import Cookie from 'js-cookie';
const navLinks = [
	{title:'Home', path:'/'},
	{ title: `About us`, path: `/about` },
	{ title: `Product`, path: `/product/all` },
	{ title: `FAQ`, path: `/faq` }	
];

function Header(){
	const [loggedin,setauth]=useState(false);
	const [type,settype]=useState('');
	useEffect(()=>{
		if(Cookie.get('session_on')==='true'){
			setauth(true);
			settype(Cookie.get('type'));
		}else{
			setauth(false);
			settype('');
		}
	},[loggedin,type])
    return (
	  		<div>
		  		<header>
              		<Navbar collapseOnSelect expand="lg" bg="dark" variant="light">
                		<Navbar.Brand href="/">Opticonnect</Navbar.Brand>
                    	<Navbar.Toggle aria-controls="responsive-navbar-nav" />
	                    <Navbar.Collapse id="responsive-navbar-nav">
                    		<Nav className="mr-auto"/>
	                        	
								{(type==='customer' || type===undefined)?navLinks.map(({title, path})=>(
    						    	<Nav.Link href={path} key={title}>{title}</Nav.Link>	
					        	)):null}
								{console.log(typeof type)}
								{ loggedin && type==='customer' ? <Nav.Link href="/cart" key="cart">Cart</Nav.Link> : null }
								{ loggedin && type==='customer' ? <Nav.Link href="/yorders" key="your orders">Your Orders</Nav.Link> : null }
								{ loggedin && type==='seller' ? <Nav.Link href="/recievedorders" key="recieved order">Recieved Orders</Nav.Link> : null }
								{ loggedin && type==='seller' ? <Nav.Link href="/insertproduct" key="insert product">Insert Product</Nav.Link> : null }
								{ loggedin && (type==='customer' || type==='seller') ? <Nav.Link href="/logout" key="logout">Logout</Nav.Link> : <Nav.Link href="/login" key="login">Signup/Login</Nav.Link> }
                    	</Navbar.Collapse>
	                </Navbar>
				</header>
      		</div>
    );
  }


export default Header;
