import React from 'react';
import axios from 'axios';
import Loadingspinner from './Loadingspinner';
import {useSelector, useDispatch} from 'react-redux';
export default function Register_product (props){
    const {productname,producttype,productprice,brand,productdescription} = useSelector(state => state.product);
    const dispatch = useDispatch();
    const submit = (e) =>{
        e.preventDefault();
        let component=this;
        const formData = new FormData();
        formData.append( "image", this.state.mainimage);
        formData.append( "image", this.state.frontimage);
        formData.append( "image", this.state.leftimage);
        formData.append( "image", this.state.rightimage);
        formData.append( "producttype", producttype);
        formData.append( "productname", productname);
        formData.append( "productprice", productprice);
        formData.append( "brand", brand);
        formData.append( "productdescription", productdescription);
        component.setState({sent:true});
        axios({
            method: 'POST',
            url: 'http://localhost:5000/product/create',
            config:{ headers: {'Content-Type':'multipart/form-data'}},
            withCredentials:true,
            data: formData
          }).then(function (response) {
            component.setState({sent:false});
            console.log(response);
          }).catch(function (err) {
            component.setState({sent:false});
            console.log(err);
        });        
    }
    if(this.state.sent===false){
        return (
    		<div className='container'>
                <div className='row justify-contents-start'>
                    <div className='col-sm'></div>
                    <div className='col-sm' >
                        <form onSubmit={submit}>
                            <input className='form-control' type="input" placeholder="Product Name" name="productname" value={this.state.productname} onChange={e => dispatch(setValue({name:e.target.name, value:e.target.value}))} required/><br/>
                            <select className='form-select' name="producttype" value={this.state.producttype} onChange={e => dispatch(setValue({name:e.target.name, value:e.target.value}))} required>
                                <option value='spectacles'>Spectacles</option>
                                <option value='sunglasses'>Sunglasses</option>
                                <option value='contact_lens'>Contact Lens</option>
                            </select><br/>
                            <input className='form-control' type="input" placeholder="Product Brand" name="brand" value={this.state.brand} onChange={ e => dispatch(setValue({name:e.target.name, value:e.target.value}))} required/><br/>
                            <input className='form-control' type="input" placeholder="Product Price" name="productprice" value={this.state.productprice} onChange={e => dispatch(setValue({name:e.target.name, value:e.target.value}))} required/><br/>
                            <textarea id='mainImage' className='form-control' type="textarea" placeholder="Product Description" name="productdescription" value={this.state.productdescription} onChange={e => dispatch(setValue({name:e.target.name, value:e.target.value}))} required></textarea><br/>
                            <label className='form-label' htmlFor='mainImage'>Main Image</label><input className='form-control' type='file' id='mainImage' name="mainimage"  required/><br/>
                            <label className='form-label' htmlFor='frontImage'>Front view</label><input className='form-control' type='file' id='frontImage' name="frontimage" required/><br/>
                            <label className='form-label' htmlFor='leftImage'>Left view</label><input className='form-control' type='file' id='leftImage' name="leftimage"  required/><br/>
                            <label className='form-label' htmlFor='rightImage'>Right view</label><input className='form-control' type='file' id='rightImage' name="rightimage"  required/><br/>
                            <center><button type="submit" className="btn btn-primary">Insert</button></center>
                        </form>
                    </div>
                    <div className='col-sm'></div>
                </div>
			</div>
		);
    }else{
        return(<Loadingspinner/>);
    }
}
