import React from 'react';
import Showprescription from './showprescription';
import axios from 'axios';
import { Link } from 'react-router-dom';
import Button from 'react-bootstrap/Button';
class Cart extends React.Component{
	constructor(props){
		super(props);
		this.state={
			cart:[],
			lens_details:[]
		}
		this.handleOnClick=this.handleOnClick.bind(this);
	}
	handleOnClick(e){
		var id=document.getElementById('delete').value;
		axios({
			method: 'DELETE',
			url: 'http://localhost:4000/cart/delete',
			withCredentials: true,
			data:{
				'pid':''+id
			}
		  }).then((response) => {
			this.setState({cart:response.data[0]['cart']});
		  }, (error) => {
				alert(error);
		  });
	}
	componentDidMount(){
		const fetchcart = () => {
			axios({
				method: 'GET',
				url: 'http://localhost:4000/cart/getcart',
				withCredentials: true,
			  }).then((response) => {
				this.setState({cart:response.data[0]['cart']});
			  }, (error) => {
					window.location='http://localhost:3000/login';
			  });
	   };
		fetchcart();
	}
	render(){
		return (
			<div style={{'height':'100vh','maxWidth':'100%'}}>
				<div style={{'border':'1px solid black','margin':'5px'}}><br/>
					<div style={{'display':'flex','width':'100%','padding':'5px'}}>
						<div style={{'width':'25%','border':'1px solid black'}}>
							<center>Product name</center>
						</div>
						<div style={{'width':'15%','border':'1px solid black'}}>
							<center>Quantity</center>
						</div>
						<div style={{'width':'15%','border':'1px solid black'}}>
							<center>Price</center>
						</div>
						<div style={{'width':'20%','border':'1px solid black'}}>
							<center>Total Price</center>
						</div>
						<div style={{'width':'20%','border':'1px solid black'}}>
							<center>Lens Details</center>
						</div>
					</div>
					{this.state.cart.length > 0 ?
						this.state.cart.map(e => {
						if(e!==null)
						return(
							<div style={{'display':'inline-flex','width':'100%','margin':'5px'}}>
								<div style={{'width':'25%','border':'1px solid black'}}>
									<center><Link to={"/productdisplay/"+e._id}>{e.productname}</Link></center>
								</div>
								<div style={{'width':'15%','border':'1px solid black'}}>
									<center>{e.quantity}</center>
								</div>
								<div style={{'width':'15%','border':'1px solid black'}}>
									<center>{e.productprice}</center>
								</div>
								<div style={{'width':'20%','border':'1px solid black'}}>
									<center><Showprescription props={e.lens_details}/></center>
								</div>
								<div style={{'width':'20%','border':'1px solid black'}}>
									<center>{parseInt(e.productprice)*e.quantity}</center>
								</div>
								<div style={{'width':'20%','border':'1px solid black'}}>
									<center><Button value={e._id} onClick={this.handleOnClick} id="delete">Delete</Button></center>
								</div>
							</div>
						)
					}):null}
				</div>
			</div>
		)
	};
}
export default Cart;
