import React, { useState }from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Cookies from 'js-cookie';
function Login(){
    
    //for user registration
    const [name,setname]=useState('');
    const [address,setaddress]=useState('');
    const [regemail,setemail]=useState('');
    const [regpassword,setregpassword]=useState('');
    const [confirmpassword,setconfirmpassword]=useState('');
    const [regusername,setregusername]=useState('');
    const [phone,setphone]=useState('');
    
    const submitValueRegister = () => {
        if (typeof regpassword !== "undefined" && typeof confirmpassword !== "undefined") {  
            if (regpassword !== confirmpassword) {
              document.getElementById('error').style.display="Visible";
            }
        }else{
            document.getElementById('error').style.display="none";
            axios({
                method: 'POST',
                url: 'http://localhost:5000/user/login',
                data: {
                    'name' : name,
                    'username' : regusername,
                    'phone' : phone,
                    'email' : regemail,
                    'password' : regpassword,
                    'address': address
                }
              });
        }
    }
    // for login
    const [logusername,setlogusername]=useState('');
    const [logpassword,setlogpassword]=useState('');
    const [typeofuser,settypeofuser]=useState('Customer');
    const submitValueLog = () => {
        axios({
            method: 'POST',
            url: 'http://localhost:5000/auth/signin',
            withCredentials: true,
            data: {
                'username' : logusername,
                'password' : logpassword,
                'typeofuser':typeofuser
            }
          }).then((response) => {
              if(Cookies.get('type')==='Customer')
                    window.location='http://localhost:3000/';
                else
                window.location='http://localhost:3000/insertproduct';
          }, (error) => {
                alert(error);
          });
    }
    return (
        <div style={{'height':'100vh'}}>
            <div style={{'width':'100%','height':'10%'}}></div>
            <div style={{'display':'flex','alignItems':'baseline'}}>
                <div >
                    <h2>Login to your account</h2>
                    <form onSubmit={submitValueLog}>
                        <input className='form-control' placeholder='Username' id='username' type='text' onChange={e => setlogusername(e.target.value)}/>
                        <input className='form-control' placeholder='Password' id='password' type='password' onChange={e => setlogpassword(e.target.value)} required/>
                        <br/>
                        <div>
                            <input type='radio' name='customer' id='customer' value='customer' label='Customer' inline onChange={e => settypeofuser(e.target.value)}/>
                            <input type='radio' name='seller' id='seller' value='seller' label='Seller' inline onChange={e => settypeofuser(e.target.value)}/>
                        </div>
                        <a href="#">Forgot Password?</a><br/>
                        <button tag='input' type='submit' value='Submit'/>
                    </form>
                </div>
            </div>
            <div>
                <h2>OR</h2>
            </div>
            <div>
                <div >
                    <h2>New User Signup!</h2>
                    <form onSubmit={submitValueRegister}>
                        <input type="text" label="Name" onChange={e => setname(e.target.value)} required/><br/>
                        <input type="email" label="Email"  onChange={e => setemail(e.target.value)} required/><br/>
                        <input type="text" label="Username" onChange={e => setregusername(e.target.value)} required/><br/>
                        <input type="password" label="Password" onChange={e => setregpassword(e.target.value)} required/><br/>
                        <input type="password" label="Confirm Password" onChange={e => setconfirmpassword(e.target.value)} required/><br/>
                        <input type="tel" label="Phone" onChange={e => setphone(e.target.value)} required/><br/>
                        <input as="textarea" label="Address" onChange={e => setaddress(e.target.value)}/><br/><br/>
                        <small><Link to="/registerseller">Want to sell with us ?</Link></small>
                        <button type='submit' value='Signup' tag='Signup'/>
                    </form>
                </div>
            </div>
        </div>                    
    );
}
export default Login;
