import React, { useEffect } from 'react';
import {Navbar,Nav} from 'react-bootstrap';
import ls from 'local-storage';
import axios from 'axios';
const navLinks = [
	{title:'Home', path:'/'},
	{ title: `About us`, path: `/about` },
	{ title: `Product`, path: `/product/all` },
	{ title: `Cart`, path: `/cart` },
	{ title: `FAQ`, path: `/faq` }	
];

function Header(){
	useEffect(()=>{
		axios({
			method: 'GET',
			url: 'http://localhost:4000/auth/verify',
			withCredentials:true
		  }).then((response) => {
			  if(response.data==='not authenticated'){
				  ls.remove('session_on');
				  ls.remove('username');
			  }
		  }, (error) => {
			
		  });
	});
    return (
	  		<div>
		  		<header>
              		<Navbar collapseOnSelect expand="lg" bg="dark" variant="light">
                		<Navbar.Brand href="/">Opticonnect</Navbar.Brand>
                    	<Navbar.Toggle aria-controls="responsive-navbar-nav" />
	                    <Navbar.Collapse id="responsive-navbar-nav">
                    		<Nav className="mr-auto"/>
	                        	{navLinks.map(({title, path})=>(
    						    	<Nav.Link href={path} key={title}>{title}</Nav.Link>	
					        	))}
								{ ls.get('session_on')==='yes' ? <Nav.Link href="/yorders" key="your orders">Your Orders</Nav.Link> : null }
								{ ls.get('session_on')==='yes' ? <Nav.Link href="/logout" key="logout">Logout</Nav.Link> : <Nav.Link href="/login" key="login">Signup/Login</Nav.Link> }
								
                    	</Navbar.Collapse>
	                </Navbar>
				</header>
      		</div>
    );
  }


export default Header;
