import React, { useEffect, useState } from 'react';
import axios from 'axios';
function ProductDisplay({ match }){
	const[product,setproduct]=useState('');
	const[shopname,setshop]=useState('');	
	var pid=match.params.productId;
	useEffect(() => {
		const fetchproduct = (id) => {
			axios({
				method: 'GET',
				url: 'http://localhost:4000/product/getsingle?id='+id,
				withCredentials: true,
			  }).then((response) => {
				setproduct(response.data);
				setshop(response.data[0]['shop']);
			  }, (error) => {
					alert(error);
			  });
	   };
		fetchproduct(pid);
	},[setproduct,pid,console.log]);
	
	return (
		<div>
			{console.log(product)}
			{product!=='' ?
			
			<div>
				<div style={{'width':'30%'}}></div>
				<div style={{'width':'70%'}}>
					<div style={{'width':'100%'}}>
						<h4>{product[0]['productname']}</h4>
					</div>	
					<div style={{'width':'100%'}}>
						<h6>Brand: {product[0]['brand']}</h6>
					</div>
					<div style={{'width':'100%'}}>
						<h6>Sold by: {shopname[0]['shopname']}</h6>
					</div>
					
					<div style={{'width':'100%'}}>
						<h6>Price: {product[0]['price']}</h6>
					</div>	
				</div>
			</div>:null}
		</div>
	);
}

export default ProductDisplay;
