import React,{useState,useEffect} from 'react';
import Sidefilter from './sidefilter';
import axios from 'axios';
import Single_product_list from './single_product_list';
function Product({ match }){
	const [products,setproducts]=useState('');
	const [filter,setFilter]=useState();
	useEffect(() => {
		const fetchproducts = () =>{
			axios({
				method: 'GET',
				url: 'http://localhost:4000/product/'+match.params.type,
			}).then((response) => {
				const final_data=response.data;
				setproducts(final_data);
			  }, (error) => {
					alert(error);
			  });
		}
		fetchproducts();
	},[setproducts,match.params.type]);
	return (
		<div style={{'width':'100%','display':'flex'}}>
			<div style={{'width':'20%','height':'100vh','display':'flex'}}>
				<Sidefilter onSubmit={()=>setFilter()}/>
			</div>
			<div style={{'width':'75%','height':'100vh','marginLeft':'5%'}}>
				<Single_product_list data={products}/>
			</div>
		</div>
	);
}
export default Product;
