import React from 'react';
import {Navbar,Nav} from 'react-bootstrap';
import jwt from 'jsonwebtoken';
import axios from 'axios';
import Cookies from 'js-cookie';
const navLinks = [
	{title:'Home', path:'/'},
	{ title: `About us`, path: `/about` },
	{ title: `Product`, path: `/product/all` },
	{ title: `Cart`, path: `/cart` },
	{ title: `FAQ`, path: `/faq` }
	
];
function getlast(){
	const token=Cookies.get('token');
	axios({
		method: 'get',
		url: 'http://localhost:4000/auth/verify',
		headers: {"Authorization" : `Bearer ${token}`}
	  }).then((response) => {
		console.log(response);
			<Nav.Link href="/login" key="login">Signup/Login</Nav.Link>
	  }, (error) => {
			<Nav.Link href="/logout" key="logout">Logout</Nav.Link>
	  });
}
function Header(){
    return (

	  		<div>
		  	<header>
              <Navbar collapseOnSelect expand="lg" bg="dark" variant="light">
                <Navbar.Brand href="/">Opticonnect</Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                    <Nav className="mr-auto"/>
                        	{navLinks.map(({title, path})=>(
    						    <Nav.Link href={path} key={title}>{title}</Nav.Link>	
					        ))}
							{
								getlast()
							}
                    </Navbar.Collapse>
                </Navbar>
			</header>
      	</div>
    );
  }


export default Header;
