import React from 'react';
import axios from 'axios';
class Yorders extends React.Component{
	constructor(props){
		super(props);
		this.state={
			transaction:[]
		}
	}
	componentDidMount(){
		axios({
			method: 'GET',
			url: 'http://localhost:4000/order/getall',
			withCredentials:true
		  }).then((response) => {
			  this.setState({transaction:response.data});
		  }, (error) => {
			
		  });
	}
	render(){
		return (
			<div>
				<div style={{'height':'1vh'}}></div>
				<div style={{'width':'100%','display':'inline-flex'}}>
					<div style={{'width':'10%'}}></div>
					<div style={{'width':'80%','border':'1px solid black'}}>
						{this.state.transaction.map((t)=>{
							return(
								<div>
									<div style={{'width':'100%'}}>
										<h4>Transaction ID# :{t._id}</h4>
									</div>
									<div style={{'width':'100%'}}>
										<h6>Date: {t.date}</h6>
									</div>
									<div style={{'width':'100%'}}>
										<p>Purchased Products: {t.products.length}</p>
									</div>
								</div>
							)
						})}
					</div>
					<div style={{'width':'10%'}}></div>
				</div>
			</div>
		);
	}
}

export default Yorders;
