import React, { useEffect, useState } from 'react';

function ProductDisplay({ match }){
	const[product,setproduct]=useState('');
	useEffect(() => {
		axios({
			method: 'GET',
			url: 'http://localhost:4000/product/getsingle?id='+match.params.productId,
			withCredentials: true,
		  }).then((response) => {
			const final_data=response.data;
			setproduct(final_data);
		  }, (error) => {
				alert(error);
		  });	
	});
	
	return (
		<div>
			<h1>{match.params.productId}</h1>
			<div>
			<div style={{'width':'30%'}}></div>
			<div style={{'width':'70%'}}>
				<div style={{'width':'100%'}}>
					<h4>{product.productname}</h4>
				</div>
				<div style={{'width':'100%'}}>
					<h6>Sold by: {product.shop.sellername}</h6>
				</div>
				<div style={{'width':'100%'}}>
					<h6>Brand: {product.brand}</h6>
				</div>
				<div style={{'width':'100%'}}>
					<h6>Sold by: {product.productprice}</h6>
				</div>
			</div>
			</div>
		</div>
	);
}

export default ProductDisplay;
