import axios from 'axios';
import React,{ useEffect, useRef } from 'react';
function Logout(){
    const loggedout=useRef(false);
    useEffect(()=>{
        axios({
            method: 'GET',
            url: 'http://localhost:4000/auth/signout',
            withCredentials: true,
        }).then((response) => {
            loggedout.current=true;
        }, (error) => {
            loggedout.current=false;
        });
        return () => {loggedout.current = false;}
    },[]);
      return(
          <div>
              { loggedout.current===true ? window.location='http://localhost:3000/' : window.location='http://localhost:3000/login'}
          </div>
      );
  }


export default Logout;
