import axios from 'axios';
import React from 'react';
export default class trackinginput extends React.Component{
	constructor(props){
		super(props);
		this.state={
			orders:[],
			fetched:'loading',
			awb:'',
			delivery_partner:'',
			error:''
		}
		this.handleOnChange=this.handleOnChange.bind(this);
		this.handleOnClick=this.handleOnClick.bind(this);
	}
	handleOnClick(e){
		e.preventDefault();
		if(this.state.awb!=='' && this.state.delivery_partner!==''){
			this.setState({fetched:'loading'});
			axios({
				url:'http://localhost:5000/order/setdelivery',
				withCredentials:true,
				data:{
					product_id:e.target.dataset.value,
					transaction_id:e.target.value,
					awb:this.state.awb,
					delivery_partner:this.state.delivery_partner
				}
			}).then((response)=>{
				this.setState({fetched:true});
				this.setState({orders:response.data});
				if(response.data.length===0){
					this.setState({error:'No pending orders'});
				}
			}).catch((error)=>{
				this.setState({fetched:true});
				if(error!==undefined){
					if(error.response.status===401){
						window.location='http://localhost:3000/login';
					}else if(error.response.status===500){
						this.setState({error:'Internal server error'});
					}
				}
			});
		}
	}
	handleOnChange(e){
		const name=e.target.name;
		this.setState({[name]:e.target.value});
	}
	render(){
		return (
            <div></div>
        );
	}
}
