import React from 'react';
import Showprescription from './showprescription';
import axios from 'axios';
class Cart extends React.Component{
	constructor(props){
		super(props);
		this.state={
			cart:[],
			lens_details:[]
		}
	}
	componentDidMount(){
		const fetchcart = () => {
			axios({
				method: 'GET',
				url: 'http://localhost:4000/cart/getcart',
				withCredentials: true,
			  }).then((response) => {
				this.setState({cart:response.data[0]['cart']});
			  }, (error) => {
					alert(error);
			  });
	   };
		fetchcart();
	}
	render(){
		return (
			<div style={{'border':'1px solid black','margin':'5px'}}><br/>
				<div style={{'width':'100%'}}>
					<div style={{'width':'25%','border':'1px solid black'}}>
						<center>Product name</center>
					</div>
					<div style={{'width':'15%','border':'1px solid black'}}>
						<center>Quantity</center>
					</div>
					<div style={{'width':'15%','border':'1px solid black'}}>
						<center>Price</center>
					</div>
					<div style={{'width':'20%','border':'1px solid black'}}>
						<center>Total Price</center>
					</div>
					<div style={{'width':'20%','border':'1px solid black'}}>
						Lens Details
					</div>
				</div>
				{this.state.cart.length > 0 ?
					this.state.cart.map(e => {
					return(
						<div style={{'display':'inline-flex','width':'100%','margin':'5px'}}>
							<div style={{'width':'25%','border':'1px solid black'}}>
								<p>{e.productname}</p>
							</div>
							<div style={{'width':'15%','border':'1px solid black'}}>
								{e.quantity}
							</div>
							<div style={{'width':'15%','border':'1px solid black'}}>
								{e.productprice}
							</div>
							<div style={{'width':'20%','border':'1px solid black'}}>
								{parseInt(e.productprice)*e.quantity}
							</div>
							<div style={{'width':'20%','border':'1px solid black'}}>
								<center><Showprescription props={e.lens_details}/></center>
							</div>
						</div>
					)
				}):null}
				
			</div>
		)
	};
}
export default Cart;
