import React,{useState,useEffect} from 'react';
import Sidefilter from './sidefilter';
import axios from 'axios';
import Singleproductlist from './single_product_list';
function Product({ match }){
	const [products,setproducts]=useState('');
	useEffect(() => {
		const fetchproducts = () =>{
			axios({
				method: 'GET',
				url: 'http://localhost:4000/product/'+match.params.type,
			}).then((response) => {
				const final_data=response.data;
				setproducts(final_data);
			  }, (error) => {
					//alert(error);
			  });
		}
		fetchproducts();
	},[setproducts,match.params.type]);
	return (
		<div className='container'>
			<div className='row justify-contents-start'>
				<div className="modal-dialog modal-fullscreen-sm-down">
					<button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#sortnfilter">Filter/Sort</button>
					<div class="modal fade" id="sortnfilter" tabindex="-1" aria-labelledby="sortnfilterlabel" aria-hidden="true">
  						<div class="modal-dialog">
    						<div class="modal-content">
      							<div class="modal-header">
        							<h5 class="modal-title" id="sortnfilterlabel">Modal title</h5>
        							<button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      							</div>
      							<div class="modal-body">
        							...
      							</div>
      							<div class="modal-footer">
        							<button type="button" class="btn btn-secondary" data-bs-dismiss="modal">Close</button>
        							<button type="button" class="btn btn-primary">Save changes</button>
      							</div>
    						</div>
  						</div>
					</div>
				</div>
				<div className='col-10'>
					<Singleproductlist data={products}/>	
				</div>
			</div>
		</div>
	);
}
export default Product;
