import React from 'react';
import axios from 'axios';
import {Button, Form} from 'react-bootstrap';
class Register_product extends React.Component{
	constructor(props){
		super(props);
		this.state={
			product:{
                productname:'',
                producttype:'',
                productprice:0,
                brand:'',
                image:null
            }
		}
        this.handleChange=this.handleChange.bind(this);
        this.submitValueRegister=this.submitValueRegister.bind(this);
	}
    submitValueRegister(){

    }
    handleChange(e){
        const target = e.target;
        const value = target.value;
        const name = target.name;
    
        this.setState({
          [name]: value
        });

    }
	render(){
		return (
			<div style={{'height':'100vh','width':'100%'}}>
				<div style={{'height':'1vh','width':'100%','display':'flex'}}></div>
                <div style={{'width':'100%','display':'flex'}}>
				    <div style={{'width':'45%','display':'flex'}}>
                        <img src={this.state.product.image} style={{'width':'100%'}} />
                    </div>
                    <div style={{'width':'50%','display':'flex'}} >
                        <form onSubmit={this.submitValueRegister}>
                            <Form.Control type="text" placeholder="Product Name" name="productname" onChange={e => this.handleChange(e.target.value)} required/><br/>
                            <Form.Control type="text" placeholder="Product Type" name="producttype" onChange={e => this.handleChange(e.target.value)} required/><br/>
                            <Form.Control type="text" placeholder="Product Brand" name="brand" onChange={e => this.handleChange(e.target.value)} required/><br/>
                            <Form.Control type="number" placeholder="Product Price" name="productprice" onChange={e => this.handleChange(e.target.value)} required/><br/>
                            <Form.Control type="file"  name="image" onChange={e => this.handleChange(e.target.value)} required/><br/>
                            <Button variant="primary" >Insert</Button>
                        </form>
                    </div>
                </div>
			</div>
		);
	}
}

export default Register_product;
