import React, { useEffect } from 'react';
import Showprescription from './showprescription';
import { Link, Redirect } from 'react-router-dom';
import {useDispatch, useSelector} from 'react-redux';
import Loadingspinner from './Loadingspinner';
import {cartGet, cartDelete, transactionAdd} from '../slices/cart/cartSlice';
export function Cart(props){
	const dispatch= useDispatch();
	const {name,city,state,pincode,address_line_1,	address_line_2,area} = useSelector(state => state.cart);
	const checkout = (e) =>{
		e.preventDefault();
		dispatch(transactionAdd(store));
	}
	const delete_from_cart= (e) =>{
		e.preventDefault();
		dispatch(cartDelete(store))
	}
	useEffect(()=>{
		dispatch(cartGet(store));
	})
	if(this.state.inserted===true){
		return(<Redirect to='/yorders'/>);
	}else if(this.state.fetched === 'loading'){
		return(<Loadingspinner/>);
	}else{
		if(this.state.cart.length!==0){
			return (
				<div className='table-responsive'>
					<h1 className="display-6">Cart</h1>
					<table className='table table-striped'>
						<thead>
							<tr>
								<th>
									Product name
								</th>
								<th>
									Quantity
								</th>
								<th>
									Price
								</th>
								<th>
									Lens Details
								</th>
								<th>
									Total Price
								</th>
								<th>&nbsp;</th>
							</tr>
						</thead>
						<tbody>
							{this.state.cart.map((e) => (
								<tr>
									<td>
										<Link to={"/productdisplay/"+e._id}>{e.productname}</Link>
									</td>
									<td>
										{e.quantity}
									</td>
									<td>
										{e.price}
									</td>
									<td>
										<Showprescription lens={e.lens_details}/>
									</td>
									<td>
										{parseInt(e.price)*e.quantity}
									</td>
									<td>
										<button className='btn btn-danger' value={e._id} onClick={delete_from_cart} type='submit' id="delete">Delete</button>
									</td>
								</tr>
							))}
							<tr>
								<td colSpan='4'>
									Total Price =
								</td>
								<td colSpan='2'>
									{total_price}
								</td>
							</tr>
							<tr>
								<td colSpan='4'>
									Client Name
								</td>
								<td colSpan='2'>
									<input className='form-control' name='name' placeholder='Client Name' onChange={this.handleOnchange} value={name}/>
								</td>
							</tr>
							<tr>
								<td colSpan='4'>
									Delivery Address
								</td>
								<td colSpan='2'>
								<input className='form-control' name='address_line_1' placeholder='Address line 1' onChange={this.handleOnchange} value={address_line_1}/>
								<input className='form-control' name='address_line_2' placeholder='Address line 2' onChange={this.handleOnchange} value={address_line_2}/>
								<input className='form-control' name='area' placeholder='Area' onChange={this.handleOnchange} value={area}/>
								<input className='form-control' name='city' placeholder='City' onChange={this.handleOnchange} value={city}/>
								<input className='form-control' name='pincode' placeholder='Pincode' onChange={this.handleOnchange} value={pincode}/>
								<input className='form-control' name='state' placeholder='State' onChange={this.handleOnchange} value={state}/>
								</td>
							</tr>
							
							<tr>
								<td colSpan='4'></td>
								<td colSpan='2'>
									<button className='btn btn-primary' onClick={checkout} type='submit'>Proceed</button>
								</td>
							</tr>
						</tbody>
					</table>
				</div>
			);
		}else{
			return(
				<div style={{'height':'100vh'}}className="border d-flex align-items-center justify-content-center">
					<h1>Oops no items in cart!!!!</h1>
    			</div>
			);
		}
	}
}
export default Cart;
