import React, { useEffect, useState } from 'react';
import axios from 'axios';

function Header(){
	const [links,setLinks]=useState([]);
	useEffect(()=>{
		axios({
			method:'GET',
			url:'http://localhost:4000/auth/getlinks',
			withCredentials:true
		}).then((response)=>{
			setLinks(response.data);
		}).catch((error)=>{
			setLinks([]);
		})
	},[]);
    return (
		<nav className='navbar navbar-light' expand='lg'  dark style={{'backgroundColor':'#2F2FA2'}}>
            <div className='container-fluid'>
                <a className='navbar-brand' href='/'>Opticonnect</a>
				<button class="navbar-toggler" type="button" data-bs-toggle="collapse" data-bs-target="#navbarToggler" aria-controls="navbarToggler" aria-expanded="false" aria-label="Toggle navigation">
      				<span class="navbar-toggler-icon"></span>
    			</button>
                <div className='collapse navbar-collapse' id='navbarToggler'>
                    <ul right fullWidth={false} className='navbar-nav mb-2 mb-lg-0'>
							{links.length !== 0 ? links.map(({title,path})=>(
								<li className='nav-item'>
									<a className='nav-link' aria-current='page' href={path}>
										{title}
									</a>
								</li>
							)):null}
                    </ul>
                </div>
            </div>
        </nav>
    );
  }


export default Header;
