import React, { useEffect, useReducer, useRef } from 'react';
import {Navbar,Nav} from 'react-bootstrap';
import Cookie from 'js-cookie';
const navLinks = [
	{title:'Home', path:'/'},
	{ title: `About us`, path: `/about` },
	{ title: `Product`, path: `/product/all` },
	{ title: `Cart`, path: `/cart` },
	{ title: `FAQ`, path: `/faq` }	
];

function Header(){
	const loggedin=useRef(false);
	useEffect(()=>{
		if(Cookie.get('session_on')){
			loggedin.current=true;
		}
		return ()=>{loggedin.current=false;}
	},[])
    return (
	  		<div>
		  		<header>
              		<Navbar collapseOnSelect expand="lg" bg="dark" variant="light">
                		<Navbar.Brand href="/">Opticonnect</Navbar.Brand>
                    	<Navbar.Toggle aria-controls="responsive-navbar-nav" />
	                    <Navbar.Collapse id="responsive-navbar-nav">
                    		<Nav className="mr-auto"/>
	                        	{navLinks.map(({title, path})=>(
    						    	<Nav.Link href={path} key={title}>{title}</Nav.Link>	
					        	))}
								{console.log(loggedin.current)}
								{ loggedin.current===true ? <Nav.Link href="/yorders" key="your orders">Your Orders</Nav.Link> : null }
								{ loggedin.current===true ? <Nav.Link href="/logout" key="logout">Logout</Nav.Link> : <Nav.Link href="/login" key="login">Signup/Login</Nav.Link> }
								
                    	</Navbar.Collapse>
	                </Navbar>
				</header>
      		</div>
    );
  }


export default Header;
