import axios from 'axios';
import React from 'react';
export default class trackinginput extends React.Component{
	constructor(props){
		super(props);
		this.state={
			orders:[],
			fetched:'loading',
			awb:'',
			delivery_partner:'',
			error:''
		}
		this.handleOnChange=this.handleOnChange.bind(this);
		this.handleOnClick=this.handleOnClick.bind(this);
	}
	handleOnClick(e){
		e.preventDefault();
		if(this.state.awb!=='' && this.state.delivery_partner!==''){
			this.setState({fetched:'loading'});
			axios({
				url:'http://localhost:5000/order/setdelivery',
				withCredentials:true,
				data:{
					product_id:e.target.dataset.value,
					transaction_id:e.target.value,
					awb:this.state.awb,
					delivery_partner:this.state.delivery_partner
				}
			}).then((response)=>{
				this.setState({fetched:true});
				this.setState({orders:response.data});
				if(response.data.length===0){
					this.setState({error:'No pending orders'});
				}
			}).catch((error)=>{
				this.setState({fetched:true});
				if(error!==undefined){
					if(error.response.status===401){
						window.location='http://localhost:3000/login';
					}else if(error.response.status===500){
						this.setState({error:'Internal server error'});
					}
				}
			});
		}
	}
	handleOnChange(e){
		const name=e.target.name;
		this.setState({[name]:e.target.value});
	}
	render(){
		return (
            <div className="modal fade" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Modal title</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <input type='text' placeholder='AWB No.' className='form-control' name='awb' value={this.state.awb} onChange={this.handleOnChange}/>
							<input type='text' placeholder='Delivery partner' className='form-control' name='delivery_partner' value={this.state.delivery_partner} onChange={this.handleOnChange}/>
                            <button type='submit' data-value={this.props.product_id} className='btn btn-primary' value={this.props._id}>Submit</button>
                        </div>
                        <div className="modal-footer">
                            <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                        </div>
                    </div>
                </div>
            </div>
        );
	}
}
