import {createAsyncThunk, createSlice} from '@reduxjs/toolkit';
import axios from 'axios';
export const checkUsernameAvailability = createAsyncThunk(
    'seller/checkUsernameAvailability',
    async ( {getState} ) =>{
        const {Registration_username} = getState().authentication;
        if(Registration_username!==''){
            try{
                const response = axios.get('http://localhost:5000/user/availability/'+Registration_username,{withCredentials:true});
                return (await response).data;
            }catch(err){
                return Promise.reject(401);
            }
        }
    }
)
export const createSeller = createAsyncThunk(
    'seller/create',
    async ( {getState} ) =>{
        const {username, password, name, email, phone, owner, company_registration, address_line_1,address_line_2, area, pincode, city, state}=getState().seller;
        const formData = new FormData(); 
            formData.append('email',email);
            formData.append('password',password);
            formData.append('name',name);
            formData.append('owner',owner);
            formData.append('username',username);
            formData.append('phone',phone);
            formData.append('image', company_registration);
            formData.append('address',address_line_1+'\n'+address_line_2+'\n'+area+'-'+pincode+'\n'+city+'\n'+state)
            
        try{
            const response = axios.post('http://localhost:5000/seller/create',{
                formData
            },{withCredentials:true});
            return (await response).data;
        }catch(err){
            return Promise.reject(401);
        }
    }
)
const sellerSlice = createSlice({
    name:'seller',
    initialState:{
        email: '',
        password:'',
        confirm_password:'',
        type_of_user:'',
        name:'',
        owner:'',
        username:'',
        phone:'',
        city:'',
		state:'',
		pincode:'',
		address_line_1:'',
		address_line_2:'',
		area:'',
        company_registration:'',
        registrationError:'',
        passwordMatch:'',
        criteriaError:'',
        availability:'NaN'
        
    },
    reducers:{
        setValue:(state,action)=>{
            state[action.payload.name] = action.payload.value;
        },setFile:(state, action)=>{
            state[action.payload.name] = action.payload.value;
        },setpasswordMatch:(state, action)=>{
            state.passwordMatch = action.payload.value;
        },setcriteriaError:(state, action)=>{
            state.criteriaError = action.payload.value;
        },setavailability:(state, action)=>{
            state.availability = action.payload.value;
        },
    },
    extraReducers:{
        [userAuth.fulfilled]:(state, action)=>{
            console.log(action)
            state.user=action.payload.user;
            state.links=action.payload.links;
            console.log(state);
            state.isAuthenticated=true;
            return action.payload.status;
        },[userAuth.rejected]:(state, action)=>{
            state.error = true;
        },[userDeauth.fulfilled]:(state,action)=>{
            state.isAuthenticated=false;
            state.user=action.payload.user;
            state.links=action.payload.links;
            return action.payload.status;
        },[userDeauth.rejected]:(state, action)=>{
            return action.payload;
        },[createCustomer.fulfilled]:(state, action)=>{
            state.isAuthenticated=true;
            state.user=action.payload.user;
            state.links=action.payload.links;
            return action.payload.status;
        },[createCustomer.rejected]:(state, action)=>{
            state.registrationError = true;
        },[checkUsernameAvailability.fulfilled]:(state, action)=>{
            state.availability = action.payload;
        },[checkUsernameAvailability.rejected]:(state,action)=>{
            state.availability = false;
        }
    }
})
export const {setValue, setFile, setavailability, setpasswordMatch, setcriteriaError}=sellerSlice.actions;
export default sellerSlice.reducer;
