import React from 'react';
import Showprescription from './showprescription';
import axios from 'axios';
import { Link } from 'react-router-dom';

class Cart extends React.Component{
	constructor(props){
		super(props);
		this.state={
			cart:[],
			lens_details:[]
		}
		this.handleOnClick=this.handleOnClick.bind(this);
	}
	handleOnClick(e){
		var id=document.getElementById('delete').value;
		axios({
			method: 'DELETE',
			url: 'http://localhost:4000/cart/delete',
			withCredentials: true,
			data:{
				'pid':''+id
			}
		  }).then((response) => {
			this.setState({cart:response.data[0]['cart']});
		  }, (error) => {
				alert(error);
		  });
	}
	componentDidMount(){
		const fetchcart = () => {
			axios({
				method: 'GET',
				url: 'http://localhost:4000/cart/getcart',
				withCredentials: true,
			  }).then((response) => {
				this.setState({cart:response.data[0]['cart']});
			  }, (error) => {
					window.location='http://localhost:3000/login';
			  });
	   };
		fetchcart();
	}
	render(){
		if(this.state.cart.length!==0){
			return (
				<div className='table table-bordered border-dark table-responsive'>
					<table className='table'>
						<thead>
							<tr>
								<th>
									<center>Product name</center>
								</th>
								<th>
									<center>Quantity</center>
								</th>
								<th>
									<center>Price</center>
								</th>
								<th>
									<center>Total Price</center>
								</th>
								<th>
									<center>Lens Details</center>
								</th>
							</tr>
						</thead>
						<tbody>
							{this.state.cart.map((e) => (
								<tr>
									<td>
										<center><Link to={"/productdisplay/"+e._id}>{e.productname}</Link></center>
									</td>
									<td>
										<center>{e.quantity}</center>
									</td>
									<td>
										<center>{e.productprice}</center>
									</td>
									<td>
										<center><Showprescription props={e.lens_details}/></center>
									</td>
									<td>
										<center>{parseInt(e.productprice)*e.quantity}</center>
									</td>
									<td>
										<center><button value={e._id} onClick={this.handleOnClick} type='submit' id="delete">Delete</button></center>
									</td>
								</tr>
							))}
						</tbody>
					</table>
				</div>
			);
		}else{
			<div style={{'height':'100vh'}}className="border d-flex align-items-center justify-content-center">
				<h1>Oops no items in cart!!!!</h1>
        	</div>
		}
		
	};
}
export default Cart;
