import React from 'react';
import showprescription from './showprescription';
class Cart extends React.Component{
	constructor(props){
		super(props);
		this.state={
			cart:[],
			lens_details:[]
		}
	}
	componentDidMount(){
		const fetchcart = () => {
			axios({
				method: 'GET',
				url: 'http://localhost:4000/cart/getcart?id='+this.props.match.params.productId,
				withCredentials: true,
			  }).then((response) => {
				this.setState({product:response.data});
				this.setState({lens_details:response.data[0]['shop']});
			  }, (error) => {
					alert(error);
			  });
	   };
		fetchcart();
	}
	render(){
		return (
			<div>
				{(this.state.cart.length!==0 || this.state.cart.length!==0)?
				this.state.cart.map((e) => {
					<div>
						<div style={{'width':'30%'}}>
							{e['productname']}
						</div>
						<div style={{'width':'15%'}}>
							{e['quantity']}
						</div>
						<div style={{'width':'15%'}}>
							{e['productprice']}
						</div>
						<div style={{'width':'20%'}}>
							{parseInt(e['productprice'])*e['quantity']}
						</div>
						<div style={{'width':'20%'}}>
							<showprescription lens={e['lens_details']}/>	
						</div>
					</div>
				}):null}
				
			</div>
		)
	};
}
