import React from 'react';
import {Button, Form,} from 'react-bootstrap';

class Register_seller extends React.Component {
    constructor(props) {
      super(props);
      this.state = {email: '',
                    password:'',
                    confirm_password:'',
                    type_of_user:'',
                    name:'',
                    owner:'',
                    username:'',
                    phone:'',
                    company_registration:''};
        this.fileInput = React.createRef();
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this);
       
    }
    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        this.setState({[name]: value});
    }
    handleSubmit(event) {

    }

    render(){
        return (
            <div>
    	        <div >
    		    	<div>
    			    	<div >
                            <div style={{'width':'30%','display': 'inline-block','verticalAlign':'middle','textAlignLast':'center'}}></div>
		    	    	    <div style={{'width':'35%','display': 'inline-block','verticalAlign':'middle','textAlignLast':'center'}}>
    					    	<h2>New Seller Signup!</h2>
    						    <form action="conn_reg_seller.php" method="post" enctype="multipart/form-data" onSubmit={this.handleSubmit}><br/>
    				    			<Form.Control  type="text" placeholder="Username" name="username" required /><br/>
	        		    			<Form.Control  placeholder="Email Address" type="email" name="email" required /><br/>
		            				<Form.Control  name="phone" placeholder="Phone Number" type="tel" pattern="[0-9]{10}" required/><br/>
	    	        				<Form.Label component="legend">Company Registration Certificate</Form.Label><Form.Control type="File" name="company_registration" required accept="image/*" ref={this.fileInput}/><br/>
    			        			<Form.Control  type="text" placeholder="Seller Name" name="seller_name" required /><br/>
					    		    <Form.Control  type="text" placeholder="Owner Name" name="owner_name" required /><br/>
					        	    <Form.Control  type="password" placeholder="Password" name="password" required id="pass" onkeyup={this.checkpass}/><i id="wrong" style={{'display': 'none'}}>Password Do not match..</i><br/>
            					    <Form.Control  name="password" placeholder="Confirm Password" required onkeyup={this.checkpass} id="confirm" /><br/>
	    						    <Button variant="outline-primary">Signup</Button>
    	        				</form>
			        		</div>
                            <div style={{'width':'35%','display': 'inline-block','verticalAlign':'middle','textAlignLast':'center'}}></div>
    			        </div>
        		    </div>
            	</div>
            </div>
        );
        }
}

export default Register_seller;
