import React, { useEffect } from 'react';
import axios from 'axios';
import Enterprescription from './enterprescription';
import Carousel from './carousel';
import Alert from './alert';
import { useDispatch, useSelector } from 'react-redux';
import { cartAdd } from '../slices/cart/cartSlice';
import store from '../store';
import { productGetSingle } from '../slices/product/productSlice';
export default function ProductDisplay(props){
	const dispatch = useDispatch();
	const {singleProduct, shopname} = useSelector(state => state.product);
	const submit = (event) =>{
		dispatch(cartAdd(store));
	}
	useEffect(()=>{
		dispatch(productGetSingle(props.match.params.productId));	
		// eslint-disable-next-line
	},[]);
	return (
		<div className='container'>
			{singleProduct.length!==0 && shopname.length!==0?
			<div className='row justify-contents-start'>
				<div className='col-sm align-self-center'>
					<Carousel images={singleProduct[0]['image']}/>
				</div>
				<div className='col-sm'>
					<div>
						<small className='text-muted'><h1 className='display-6'>{singleProduct[0]['productname']}</h1></small> 
					</div>	
					<div>
						<h6>Brand: <small className='text-muted'>{singleProduct[0]['brand']}</small></h6>
					</div>
					<div>
						<h6>Sold by: <small className='text-muted'>{shopname[0]['shopname']}</small></h6>
					</div>
					<div>
						<h6>Price: <small className='text-muted'>{singleProduct[0]['price']}</small></h6>
					</div>	
					<div>
						{console.log(this.state)}
						<h6>{singleProduct[0]['productdescription']}</h6>
					</div>
					<Enterprescription lens={this.state} handleSubmit={submit}/>	
					{this.state.error!==''?<Alert message={this.state.error} type='danger'/>:null}
				</div>
			</div>:null}
		</div>
	);
}
