import React from 'react';
import {Button, Form} from 'react-bootstrap';
import { Link, useLocation } from 'react-router-dom';
import { makeStyles } from '@material-ui/core/styles';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemText from '@material-ui/core/ListItemText';
import Divider from '@material-ui/core/Divider';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';

const useStyles = makeStyles((theme) => ({
    root: {
      width: '100%',
      maxWidth: 360,
      backgroundColor: theme.palette.background.paper,
    },
    formControl: {
        margin: theme.spacing(1),
        minWidth: 120,
    },
    sidebarouter:{
        margin:'5%',
        border:'solid',
        borderColor:'#d2d2d2',
        borderWidth:'2px',
        borderRadius:'10px',
        padding:'2%',
        width:'100%',
    }
  }));
function Sidefilter(){
    const [price, setprice] = React.useState('');
    const [sort, setsort] = React.useState('');
    const [openprice, setOpenprice] = React.useState(false);
    const [opensort, setOpensort] = React.useState(false);
    
    const handleprice = (event) => {
        setprice(event.target.value);
    };
    const handlesort = (event) => {
        setsort(event.target.value);
    };

    const handleClosesort = () => {
        setOpensort(false);
    };

    const handleOpensort = () => {
        setOpensort(true);
    };
    const handleCloseprice = () => {
        setOpenprice(false);
    };

    const handleOpenprice = () => {
        setOpenprice(true);
    };
	const classes=useStyles();
	return (
		<div>
            <div className={classes.sidebarouter}>
                <div>
				    <FormControl>
					    <h3>Categories</h3>
            		    <List component="nav" className={classes.root} aria-label="mailbox folders">
                            <ListItem button>
                                <Link to="/product"><ListItemText primary="All" /></Link>
                            </ListItem>
                            <Divider/>
                            <ListItem button>
                                <Link to="/product?type=sunglasses"><ListItemText primary="Sunglasses" /></Link>
                            </ListItem>
                            <Divider dark/>
                            <ListItem button divider>
                                <Link to="/product?type=spectacles"><ListItemText primary="Spectacles" /></Link>
                            </ListItem>
                            <ListItem button>
                                <Link to="/product?type=contact_lens"><ListItemText primary="Contact Lens" /></Link>
                            </ListItem>

                        </List>
                    </FormControl>
                    <Divider height="2px"/>
			        <div>
                    <br/><h4>
						    Filters
    					</h4>
					    <form action="/product" method="POST">
    						<div>
                                <FormControl className={classes.formControl}>
                                    <InputLabel >Age</InputLabel>
                                    <Select labelId="price-control" id="select-price" open={openprice} onClose={handleCloseprice} onOpen={handleOpenprice} value={price} onChange={handleprice}>
    								    <MenuItem value="price">Price</MenuItem>
								        <MenuItem value="0">Rs 0 - Rs 1000</MenuItem>
    								    <MenuItem value="1000">Rs 1001.00 - Rs2000</MenuItem>
	    							    <MenuItem value="2000">Rs 2001.00 - Rs 3000</MenuItem>
		    						    <MenuItem value="3000">Rs 3001.00 - Rs 4000</MenuItem>
    			    					<MenuItem value="4000">Rs 4000+</MenuItem>
				    			    </Select>
                                </FormControl>
    						</div><br/>
	    					<div>
		    					<div>
			    					<div>
                                    <FormControl className={classes.formControl}>
                                        <InputLabel >No Sorting</InputLabel>
                                        <Select labelId="sort-control" id="select-sort" open={opensort} onClose={handleClosesort} onOpen={handleOpensort} value={sort} onChange={handlesort}>
					    					<MenuItem value="up">Price: low to high</MenuItem>
							    			<MenuItem value="down">Price: high to low</MenuItem>
								    	</Select>
                                    </FormControl>
                                    
    								</div>
	    						</div>
		    				</div><br/>	
                            <Divider height='3px'></Divider>		
					        <div style={{'text-align':'center'}}>
                                <br/><Form.Control type="text" name="search" placeholder="Search Products..." style={{'text-align':'left'}}/><br/>
	    					    <Button >Search</Button>
                            </div><br/>
				        </form>
                    </div>
                </div>
			</div>
        </div>
	);
}

export default Sidefilter;
