import React from 'react';
import axios from 'axios';
import {Button, Form,FormFile} from 'react-bootstrap';
class Register_product extends React.Component{
	constructor(props){
		super(props);
		this.state={
                productname:'',
                producttype:'',
                productprice:0,
                brand:'',
                productdescription:'',
                image:null
		}
        this.baseState=this.state;
        this.handleChange=this.handleChange.bind(this);
        this.handleFileChange=this.handleFileChange.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this);
	}
    handleFileChange(e){
        this.setState({ image:e.target.files[0] });
    }
    handleSubmit(e){
        e.preventDefault();
        const formData = new FormData(); 
        formData.append( "image", this.state.image);
        formData.append( "producttype", this.state.producttype);
        formData.append( "productname", this.state.productname);
        formData.append( "productprice", this.state.productprice);
        formData.append( "brand", this.state.brand);
        formData.append( "productdescription", this.state.brand);
        axios({
            method: 'POST',
            url: 'http://localhost:4000/product/create',
            config:{ headers: {'Content-Type':'multipart/form-data'}},
            withCredentials:true,
            data: formData
          }).then(function (response) {
            console.log(response);
          }).catch(function (err) {
            console.log(err);
        });
        this.setState(this.baseState);
        
    }
    handleChange(e){
        const target = e.target;
        const value = target.value;
        const name = target.name;
    
        this.setState({
          [name]: value
        });

    }
	render(){
		return (
			<div style={{'height':'100vh','width':'100%'}}>
				<div style={{'height':'1vh','width':'100%','display':'flex'}}></div>
                <div style={{'width':'100%','display':'flex'}}>
				    <div style={{'width':'45%','display':'flex'}}>
                        <img src={this.state.image} style={{'width':'100%','display':'flex'}} />
                    </div>
                    <div style={{'width':'50%','display':'flex','marginLeft':'10px'}} >
                        <form onSubmit={this.handleSubmit} style={{'width':'100%'}}>
                            <Form.Control as="input" placeholder="Product Name" name="productname" value={this.state.productname} onChange={this.handleChange} required/><br/>
                            <Form.Control as="input" placeholder="Product Type" name="producttype" value={this.state.producttype} onChange={this.handleChange} required/><br/>
                            <Form.Control as="input" placeholder="Product Brand" name="brand" value={this.state.brand} onChange={ this.handleChange} required/><br/>
                            <Form.Control as="input" placeholder="Product Price" name="productprice" value={this.state.productprice} onChange={this.handleChange} required/><br/>
                            <Form.Control as="textarea" placeholder="Product Description" name="productdescription" value={this.state.productdescription} onChange={this.handleChange} required/><br/>
                            <FormFile.Input   name="image" onChange={this.handleFileChange} required/><br/>
                            <center><Button variant="primary">Insert</Button></center>
                        </form>
                    </div>
                </div>
			</div>
		);
	}
}

export default Register_product;
