import React from 'react';
import { Link} from 'react-router-dom';
import Divider from '@material-ui/core/Divider';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import InputLabel from '@material-ui/core/InputLabel';
import FormControl from '@material-ui/core/FormControl';

function Sidefilter(){
    const [price, setprice] = React.useState('');
    const [sort, setsort] = React.useState('');
    const [openprice, setOpenprice] = React.useState(false);
    const [opensort, setOpensort] = React.useState(false);
    
    const handleprice = (event) => {
        setprice(event.target.value);
    };
    const handlesort = (event) => {
        setsort(event.target.value);
    };

    const handleClosesort = () => {
        setOpensort(false);
    };

    const handleOpensort = () => {
        setOpensort(true);
    };
    const handleCloseprice = () => {
        setOpenprice(false);
    };

    const handleOpenprice = () => {
        setOpenprice(true);
    };
	return (
            <div>
                <div>
				    <form>
					    <h3>Categories</h3>
            		    <ul className='list-group'>
                            <li className='list-item'><Link to="/product/all">All</Link></li>
                            <li className='list-item'><Link to="/product/sunglasses">Sunglasses</Link></li>
                            <li className='list-item'><Link to="/product/spectacles">Spectacles</Link></li>
                            <li className='list-item'><Link to="/product/contact_lens">Contact Lens</Link></li>
                        </ul>
                    </form>
                    <hr/>
			        <div>
                        <h4>Filters</h4>
    				    <div>
                            <form action="/product" method="POST">
                                <div className='form-control'>
                                    <label className='form-label' >Price</label>
                                    <select className='form-select' labelId="price-control" id="select-price" open={openprice} onClose={handleCloseprice} onOpen={handleOpenprice} value={price} onChange={handleprice}>
    			    				    <MenuItem value="price">Price</MenuItem>
					    		        <MenuItem value="0">Rs 0 - Rs 1000</MenuItem>
						    		    <MenuItem value="1000">Rs 1001.00 - Rs2000</MenuItem>
	    					    	    <MenuItem value="2000">Rs 2001.00 - Rs 3000</MenuItem>
		    					        <MenuItem value="3000">Rs 3001.00 - Rs 4000</MenuItem>
        			    				<MenuItem value="4000">Rs 4000+</MenuItem>
	    			    			</select>
                                </div>
	    	    				<div>
		    	    		    	<div>
                                        <div className='form-control'>
                                            <label className='form-label' >No Sorting</label>
                                            <select className='form-select' labelId="sort-control" id="select-sort" open={opensort} onClose={handleClosesort} onOpen={handleOpensort} value={sort} onChange={handlesort}>
    				    		    			<MenuItem value="up">Price: low to high</MenuItem>
    						    	    		<MenuItem value="down">Price: high to low</MenuItem>
		    						        </select>
                                        </div>
        			    			</div>
	        			    	</div>	
                            </form>
                            <hr/>		
                            <form>
					            <div style={{'text-align':'center'}}>
                                    <input type="text" className='form-control' name="search" placeholder="Search Products..." style={{'text-align':'left'}}/>
	    					        <button className='btn btn-primary' type='submit' >Search</button>
                                </div>
                            </form>
                        </div>
                    </div>
		        </div>
            </div>
	);
}

export default Sidefilter;
