import React,{useState,useEffect} from 'react';
import { Link } from 'react-router-dom';
import Sidefilter from './sidefilter';
import axios from 'axios';
function Product({ match }){
	const [products,setproducts]=useState({
		product:null
	});
	let type=match.params.type;
	useEffect( () => {
		
			axios({
				method: 'GET',
				url: 'http://localhost:4000/product/'+type,
			}).then((response) => {
				const final_data=response.data.final_data;
				setproducts({product:final_data});
		  	}, (error) => {
					alert(error);
		  	});

	},[setproducts]);
	
	
	return (
		<div style={{'width':'100%','display':'flex'}}>
			<div style={{'width':'20%','height':'100vh','display':'flex'}}>
				<Sidefilter/>
			</div>
			{}
			<div style={{'width':'75%','height':'100vh','marginLeft':'5%'}}>
			{products.product.map(({})=>(
					<div>
						<div style={{'width':'40%'}}>
							<img src={image_path}/>
						</div>
						<div style={{'width':'60%'}}>
							<div style={{'width':'100%'}}>
								<Link to={"/productdisplay/"+productid} key={productid}><h4>{productname}</h4></Link>
							</div>
							<div style={{'width':'100%'}}>
								{sellername}
							</div><br/>
							<div style={{'width':'100%'}}>
								{productprice}
							</div>
						</div>
					</div>
				))}
			</div>
		</div>
	);
}

export default Product;
