import {createAsyncThunk, createSlice} from '@reduxjs/toolkit';
import axios from 'axios';
export const checkUsernameAvailability = createAsyncThunk(
    'seller/checkUsernameAvailability',
    async ( {getState} ) =>{
        const {username} = getState().seller;
        if(username!==''){
            try{
                const response = axios.get('http://localhost:5000/seller/availability/'+username,{withCredentials:true});
                return (await response).data;
            }catch(err){
                return Promise.reject(401);
            }
        }
    }
)
export const createSeller = createAsyncThunk(
    'seller/create',
    async ( company_registration, {getState} ) =>{
        const {username, password, name, email, phone, owner, address_line_1,address_line_2, area, pincode, city, state}=getState().seller;
        const address=address_line_1+'\n'+address_line_2+'\n'+area+'-'+pincode+'\n'+city+'\n'+state;
        const formData = new FormData(); 
        formData.append('email',email);
        formData.append('password',password);
        formData.append('name',name);
        formData.append('owner',owner);
        formData.append('username',username);
        formData.append('phone',phone);
        formData.append('image', company_registration);
        console.log(formData.append('address',address));
        console.log(formData);
        try{
            const response = axios.post('http://localhost:5000/seller/create'
            ,{Headers:{'Content-Type':'multipart/form-data'},withCredentials:true},{data:formData});
            return (await response).data;
        }catch(err){
            return Promise.reject(401);
        }
    }
)
const sellerSlice = createSlice({
    name:'seller',
    initialState:{
        email: '',
        password:'',
        confirm_password:'',
        name:'',
        owner:'',
        username:'',
        phone:'',
        city:'',
		state:'',
		pincode:'',
		address_line_1:'',
		address_line_2:'',
		area:'',
        registrationError:'',
        passwordMatch:'NaN',
        criteriaError:'NaN',
        availability:'NaN'
        
    },
    reducers:{
        setValue:(state,action)=>{
            state[action.payload.name] = action.payload.value;
        },setpasswordMatch:(state, action)=>{
            state.passwordMatch = action.payload;
        },setcriteriaError:(state, action)=>{
            state.criteriaError = action.payload;
        }
    },
    extraReducers:{
        [createSeller.fulfilled]:(state, action)=>{
            console.log(action)
            state.user=action.payload.user;
            state.links=action.payload.links;
            console.log(state);
            state.isAuthenticated=true;
            return action.payload.status;
        },[createSeller.rejected]:(state, action)=>{
            state.error = true;
        },[checkUsernameAvailability.fulfilled]:(state, action)=>{
            state.availability = action.payload;
        },[checkUsernameAvailability.rejected]:(state,action)=>{
            state.availability = false;
        }
    }
})
export const {setValue, setpasswordMatch, setcriteriaError}=sellerSlice.actions;
export default sellerSlice.reducer;
