import React from 'react';
import {Navbar,Nav} from 'react-bootstrap';
import axios from 'axios';
import Cookies from 'js-cookie';
const navLinks = [
	{title:'Home', path:'/'},
	{ title: `About us`, path: `/about` },
	{ title: `Product`, path: `/product/all` },
	{ title: `Cart`, path: `/cart` },
	{ title: `FAQ`, path: `/faq` }
	
];

function Header(){
	const [loggedin,setlogin]=useState(false);
	var token=Cookies.get('token');
	axios({
		method: 'get',
		url: 'http://localhost:4000/auth/verify',
		headers: {Authorization : 'Bearer '+ token}
	  }).then((response) => {
			setlogin(true);

	  }, (error) => {
			setlogin(false);
	  });
    return (

	  		<div>
		  	<header>
              <Navbar collapseOnSelect expand="lg" bg="dark" variant="light">
                <Navbar.Brand href="/">Opticonnect</Navbar.Brand>
                    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
                    <Navbar.Collapse id="responsive-navbar-nav">
                    <Nav className="mr-auto"/>
                        	{navLinks.map(({title, path})=>(
    						    <Nav.Link href={path} key={title}>{title}</Nav.Link>	
					        ))}
							{ loggedin ? <Nav.Link href="/logout" key="logout">Logout</Nav.Link> : <Nav.Link href="/login" key="login">Signup/Login</Nav.Link> }
                    </Navbar.Collapse>
                </Navbar>
			</header>
      	</div>
    );
  }


export default Header;
