import React from 'react';
import axios from 'axios';
import {Button, Form} from 'react-bootstrap';
class Register_product extends React.Component{
	constructor(props){
		super(props);
		this.state={
                productname:'',
                producttype:'',
                productprice:0,
                brand:'',
                image:null
		}
        this.handleChange=this.handleChange.bind(this);
        this.handleFileChange=this.handleFileChange.bind(this);
        this.handleSubmit=this.handleSubmit.bind(this);
	}
    handleFileChange(e){
        this.setState({ image:e.target.files[0] });
    }
    handleSubmit(){
        const formData = new FormData(); 
        formData.append( "image", this.state.selectedFile, this.state.selectedFile.name );
        console.log(this.state);
        alert(this.state);
        
    }
    handleChange(e){
        const target = e.target;
        const value = target.value;
        const name = target.name;
    
        this.setState({
          [name]: value
        });

    }
	render(){
		return (
			<div style={{'height':'100vh','width':'100%'}}>
				<div style={{'height':'1vh','width':'100%','display':'flex'}}></div>
                <div style={{'width':'100%','display':'flex'}}>
				    <div style={{'width':'45%','display':'flex'}}>
                        <img src={this.state.image} style={{'width':'100%','display':'flex'}} />
                    </div>
                    <div style={{'width':'50%','display':'flex','marginLeft':'10px'}} >
                        <form onSubmit={this.handleSubmit} style={{'width':'100%'}}>
                            <Form.Control type="text" placeholder="Product Name" name="productname" value={this.state.productname} onChange={this.handleChange} required/><br/>
                            <Form.Control type="text" placeholder="Product Type" name="producttype" value={this.state.producttype} onChange={this.handleChange} required/><br/>
                            <Form.Control type="text" placeholder="Product Brand" name="brand" value={this.state.brand} onChange={ this.handleChange} required/><br/>
                            <Form.Control type="number" placeholder="Product Price" name="productprice" value={this.state.productprice} onChange={this.handleChange} required/><br/>
                            <Form.Control type="file"  name="image" onChange={this.handleFileChange} required/><br/>
                            <center><input type="submit" variant="primary" value="Insert"/></center>
                        </form>
                    </div>
                </div>
			</div>
		);
	}
}

export default Register_product;
