import React,{useState,useEffect} from 'react';
import Sidefilter from './sidefilter';
import axios from 'axios';
import Single_product_list from './single_product_list';
function Product({ match }){
	const [products,setproducts]=useState('');
	let type=match.params.type;
	useEffect( () => {
			const fetchproducts=()=>{
				axios({
					method: 'GET',
					url: 'http://localhost:4000/product/'+type,
				}).then((response) => {
					const final_data=response.data;
					setproducts(final_data);
				  }, (error) => {
						alert(error);
				  });
			}
			fetchproducts();
	},[]);
	return (
		<div style={{'width':'100%','display':'flex'}}>
			<div style={{'width':'20%','height':'100vh','display':'flex'}}>
				<Sidefilter/>
			</div>
			<div style={{'width':'75%','height':'100vh','marginLeft':'5%'}}>
				<Single_product_list data={products}/>
			</div>
		</div>
	);
}

export default Product;
