import React,{useState,useEffect} from 'react';
import Sidefilter from './sidefilter';
import axios from 'axios';
import Singleproductlist from './single_product_list';
function Product({ match }){
	const [products,setproducts]=useState('');
	useEffect(() => {
		const fetchproducts = () =>{
			axios({
				method: 'GET',
				url: 'http://localhost:4000/product/'+match.params.type,
			}).then((response) => {
				const final_data=response.data;
				setproducts(final_data);
			  }, (error) => {
					//alert(error);
			  });
		}
		fetchproducts();
	},[setproducts,match.params.type]);
	return (
		<div className='container'>
			<div className='row justify-contents-center'>
				<div className='col-2'>
					<button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#exampleModal">Filter/Sort</button>
					<div className="modal fade"id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
						<div className='modal-dialog modal-fullscreen-sm-down'>
							<div className='modal-content'>
								<div class="modal-header">
        							<h5 class="modal-title" id="exampleModalLabel">Sort and Filter</h5>
        							<button type="button" class="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
      							</div>
								<div className='modal-body'>
									<Sidefilter/>
								</div>
							</div>
						</div>
					</div>
				</div>
			</div>
			<hr/>
			<div className='row justify-contents-start'>
				<div className='col-12'>
					<Singleproductlist data={products}/>	
				</div>
			</div>
		</div>
	);
}
export default Product;
