import React,{useState,useEffect} from 'react';
import Sidefilter from './sidefilter';
import axios from 'axios';
import Singleproductlist from './single_product_list';
function Product({ match }){
	const [products,setproducts]=useState('');
	useEffect(() => {
		const fetchproducts = () =>{
			axios({
				method: 'GET',
				url: 'http://localhost:4000/product/'+match.params.type,
			}).then((response) => {
				const final_data=response.data;
				setproducts(final_data);
			  }, (error) => {
					//alert(error);
			  });
		}
		fetchproducts();
	},[setproducts,match.params.type]);
	return (
		<div className='container'>
				<div className="modal-dialog modal-fullscreen-sm-down">
					<button type="button" className="btn btn-primary" data-bs-toggle="modal" data-bs-target="#staticBackdrop">Sort/Filter</button>
					<div className="modal fade" id="staticBackdrop" 
         					data-bs-backdrop="static" 
         					data-bs-keyboard="false" 
         					tabIndex="-1" 
         					aria-labelledby="staticBackdropLabel"
         					aria-hidden="true">
         				<div className="modal-dialog">
            				<div className="modal-content">
              					<div className="modal-header">
                					<h5 className="modal-title" id="staticBackdropLabel">Modal title</h5>
                					<button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
              					</div>
              					<div className="modal-body">
                					...
              					</div>
              					<div className="modal-footer">
                					<button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
                					<button type="button" className="btn btn-primary">Understood</button>
              					</div>
            				</div>
          				</div>
        			</div>
				</div>
			<div className='row justify-contents-start'>
				<div className='col-10'>
					<Singleproductlist data={products}/>	
				</div>
			</div>
		</div>
	);
}
export default Product;
