import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Enterprescription from './enterprescription';
class ProductDisplay extends React.Component{
	constructor(props){
		this.state={
			id:props.productId,
			lens_details:{
				rsign:'',
				lsign:'',
				resph:0,
				reax:0,
				recyl:0,
				readd:0,
				lesph:0,
				leax:0,
				lecyl:0,
				leadd:0,
			},
			product:[],
			shopname:[]
		}
		this.setproduct=this.setproduct.bind(this);
		this.setshop=this.setshop.bind(this);
		this.handleChange=this.handleChange.bind(this);
		this.handleSubmit=this.handleSubmit.bind(this);
	}
	handleSubmit(event){
		console.log(this.state.lens_details);
	}
	handleChange(event){
		const { name, value } = event.target;
		this.setState(prevState => ({
			lens_details: { ...prevState.lens_details, [name]: value }
		}));
	}
	componentDidMount(){
		const fetchproduct = (id) => {
			axios({
				method: 'GET',
				url: 'http://localhost:4000/product/getsingle?id='+this.state.id,
				withCredentials: true,
			  }).then((response) => {
				this.setproduct(response.data);
				this.setshop(response.data[0]['shop']);
			  }, (error) => {
					alert(error);
			  });
	   };
		fetchproduct(pid);
	}
	render(){
		return (
			<div>
				{console.log(this.state.product)}
				{this.state.product!=='' && this.state.shopname!==''?
				
				<div>
					<div style={{'width':'30%'}}></div>
					<div style={{'width':'70%'}}>
						<div style={{'width':'100%'}}>
							<h4>{this.state.product[0]['productname']}</h4>
						</div>	
						<div style={{'width':'100%'}}>
							<h6>Brand: {this.state.product[0]['brand']}</h6>
						</div>
						<div style={{'width':'100%'}}>
							<h6>Sold by: {this.state.shopname[0]['shopname']}</h6>
						</div>

						<div style={{'width':'100%'}}>
							<h6>Price: {this.state.product[0]['price']}</h6>
						</div>	
						<div style={{'width':'100%'}}>
							<h6>{this.state.product[0]['productdescription']}</h6>
						</div>
						<Enterprescription handleChange={this.handleChange} lens={this.state.lens_details} handleSubmit={this.handleSubmit}/>	
					</div>
				</div>:null}
			</div>
		);
	}
}

export default ProductDisplay;
