import React from 'react';
import {Route, Switch} from "react-router-dom";
import Homepage from "./Home";
import Cart from "./Cart";
import Product from "./Product";
import About from "./About";
import Faq from "./Faq";
import Login from "./login";
import Register_seller from './register_seller';
import ProductDisplay from './productdisplay';
import jwt from 'jsonwebtoken';
import Cookies from 'js-cookie';
function requireAuth(nextState, replace, next) {
  jwt.verify(token,Cookies.get('username'),function(err,decoded){
    replace({
      pathname: "/logout",
      state: {nextPathname: nextState.location.pathname}
      });
  });
	next();
}
const Mainrouter = () => {
    return (
        <Switch>
            <Route path="/" exact component={Homepage}/>
            <Route path="/about" component={About}/>
            <Route path="/cart"  component={Cart}/>
            <Route path="/product/:type"  component={Product}/>
            <Route path="/faq"  component={Faq}/>
            <Route path="/login" component={Login} onEnter={requireAuth}/>
            <Route path="/registerseller" component={Register_seller}/>
            <Route path="/productdisplay/:productId" component={ProductDisplay}/>
        </Switch>
    );
}

export default Mainrouter
