import axios from 'axios';
import React,{ useState } from 'react';
import { Redirect } from 'react-router-dom';
function Logout(){
    const [loggedout,setloggedout]=useState(false);
    axios({
        method: 'get',
        url: 'http://localhost:4000/auth/signout',
        withCredentials: true,
        data: {}
    }).then((response) => {
          setloggedout(true);
    }, (error) => {
          setloggedout(false);
    });
      return(
          <div>
              { loggedout ? window.location='http://localhost:3000/' : window.location='http://localhost:3000/login'}
          </div>
      );
  }


export default Logout;
