import React from 'react';
import axios from 'axios';
import Loadingspinner from './Loadingspinner';
import Alert from './alert';
import Carousel from './carousel';
class single_order extends React.Component{
	constructor(props){
		super(props);
		this.state={
			transaction:[],
			fetched:'loading'
		}
	}
	componentDidMount(){
		axios({
			method: 'GET',
			url: 'http://localhost:5000/transaction/'+this.props.params.id,
			withCredentials:true,
		  }).then((response) => {
			  	this.setState({fetched:true});	
				this.setState({transaction:response.data});
		  }, (error) => {
				this.setState({fetched:false});
			  	if(error){
					if(error.response.status===401){
						window.location='http:localhost:3000/logout';
					}
			  }
		  });
	}
	render(){
		if(this.state.fetched==='loading'){
			return(<Loadingspinner/>);
		}else if(this.state.fetched===true){
			return (
				<div className='row'>
					<div className='col align-self-center'>
						{this.state.transaction.map((t)=>{
							return(
                                <div class="card border-dark mb-3" style="max-width: 18rem;">
                                    <div class="card-header">{t._id}</div>
                                    <div class="card-body text-dark">
                                        <div class="accordion" id="accordionExample">
                                            {this.t.products.map((products)=>{
                                                <div class="accordion-item">
                                                <h2 class="accordion-header" id="headingOne">
                                                    <button class="accordion-button" type="button" data-bs-toggle="collapse" data-bs-target="#collapseOne" aria-expanded="true" aria-controls="collapseOne">
                                                        {products.name}
                                                    </button>
                                                    </h2>
                                                    <div id="collapseOne" class="accordion-collapse collapse show" aria-labelledby="headingOne" data-bs-parent="#accordionExample">
                                                        <div class="accordion-body">
                                                            <div class="card mb-3">
                                                                <Carousel images={products.images}/>
                                                                <div class="card-body">
                                                                    <h5 class="card-title">{products.name}</h5>
                                                                    <p class="card-text">Quantity<small class="text-muted">{products.quantity}</small></p>
                                                                    <p class="card-text">{parseInt(products.price)*parseInt(products.quantity)}</p>
                                                                </div>
                                                            </div>
                                                        </div>
                                                    </div>
                                                </div>
                                            })}
                                        </div>    
                                    </div>  
                                </div>
							)
						})}
					</div>
				</div>
			);
		}else{
			return (
				<div className='row'>
					<div className='col align-self-center'>
						<Alert message='Internal server error'/>
					</div>
				</div>
			);
		}
	}
}

export default single_order;
