import React, { useState }from 'react';
import {Button, Form} from 'react-bootstrap';
import { Link } from 'react-router-dom';
import axios from 'axios';
import ls from 'local-storage';
function Login(){
    
    //for user registration
    const [name,setname]=useState('');
    const [address,setaddress]=useState('');
    const [regemail,setemail]=useState('');
    const [regpassword,setregpassword]=useState('');
    const [confirmpassword,setconfirmpassword]=useState('');
    const [regusername,setregusername]=useState('');
    const [phone,setphone]=useState('');
    
    const submitValueRegister = () => {
        if (typeof regpassword !== "undefined" && typeof confirmpassword !== "undefined") {  
            if (regpassword !== confirmpassword) {
              document.getElementById('error').style.display="Visible";
            }
        }else{
            document.getElementById('error').style.display="none";
            axios({
                method: 'POST',
                url: 'http://localhost:4000/user/login',
                data: {
                    'name' : name,
                    'username' : regusername,
                    'phone' : phone,
                    'email' : regemail,
                    'password' : regpassword,
                    'address': address
                }
              });
        }
    }
    // for login
    const [logusername,setlogusername]=useState('');
    const [logpassword,setlogpassword]=useState('');
    const [typeofuser,settypeofuser]=useState('Customer');
    const submitValueLog = () => {
        axios({
            method: 'POST',
            url: 'http://localhost:4000/auth/signin',
            withCredentials: true,
            data: {
                'username' : logusername,
                'password' : logpassword,
                'typeofuser':typeofuser
            }
          }).then((response) => {
                window.location='http://localhost:3000/';
          }, (error) => {
                alert(error);
          });
    }
    return (
        <div style={{'height':'100vh'}}>
            <div style={{'width':'100%','height':'10%'}}></div>
            <div style={{'width':'100%','display': 'inline-block'}}>
                <div style={{'width':'30%','display': 'inline-block'}}></div>
                <div style={{'width':'40%','display': 'inline-block','textAlignLast':'center'}}>
                    <h2>Login to your account</h2>
                    <form onSubmit={submitValueLog}>
                        <Form.Control placeholder="Username" onChange={e => setlogusername(e.target.value)} required/><br/>
                        <Form.Control placeholder="Password" type="password" onChange={e => setlogpassword(e.target.value)} required/>
                        <div  key="inline-radio" className="mb-3">
                            <Form.Check inline name="typeofuser" type="radio" id="inline-radio-1" value="Customer" label="Customer" onChange={e => settypeofuser(e.target.value)}/>
                            <Form.Check inline name="typeofuser" type="radio" id="inline-radio-2" value="Seller" label="Seller" onChange={e => settypeofuser(e.target.value)}/>	
                        </div>
                        <a href="forgotpassword.php">Forgot Password?</a><br/><br/>
                        <input type="submit" variant="primary" value="Login"/>
                    </form>
                </div>
                <div style={{'width':'30%','display': 'inline-block'}}></div>
            </div>
            <div style={{'width':'100%','height':'5%'}}></div>
            <div style={{'width':'100%','display': 'inline-block','textAlignLast':'center'}}>
                <h2>OR</h2>
            </div>
            <div style={{'width':'100%','height':'5%'}}></div>
            <div style={{'width':'100%','display': 'inline-block'}}>
                <div style={{'width':'30%','display': 'inline-block'}}></div>
                <div style={{'width':'40%','display': 'inline-block','textAlignLast':'center'}}>
                    <h2>New User Signup!</h2>
                    <form action="connect_reg.php" method="post" onClick={submitValueRegister}>
                        <Form.Control type="text" placeholder="Name" onChange={e => setname(e.target.value)} required/><br/>
                        <Form.Control type="email" placeholder="Email"  onChange={e => setemail(e.target.value)} required/><br/>
                        <Form.Control type="text" placeholder="Username" onChange={e => setregusername(e.target.value)} required/><br/>
                        <Form.Control type="password" placeholder="Password" onChange={e => setregpassword(e.target.value)} required/><br/>
                        <Form.Control type="password" placeholder="Confirm Password" onChange={e => setconfirmpassword(e.target.value)} required/><br/>
                        <Form.Control type="tel" placeholder="Phone" onChange={e => setphone(e.target.value)} required/><br/>
                        <Form.Control as="textarea" placeholder="Address" onChange={e => setaddress(e.target.value)}/><br/><br/>
                        <small><Link to="/registerseller">Want to sell with us ?</Link></small>
                        <Button variant="primary" >SignUp</Button>
                    </form>
                </div>
                <div style={{'width':'30%','display': 'inline-block'}}></div>
            </div>
        </div>                    
    );
}
export default Login;
