import React from 'react';
import {Link} from 'react-router-dom';
function single_product_list(props){
    const {data}=props;
    if(data.length>0){
        return(
            <div className='row'>
                {data.map(({_id,productname,productprice,image_path,productdescription,brand})=>(
                    <div className='col align-items-center'>
                        <div class="card mb-3" style={{'max-width': '540px'}}>
                            <div class="row g-0">
                                <div class="col-md-4">
                                    <img src={image_path} alt={productname}/>
                                </div>
                                <div class="col-md-8">
                                    <div class="card-body">
                                        <h5 class="card-title"><Link to={"/productdisplay/"+_id} key={_id}><h4>{productname}</h4></Link></h5>
                                        <p class="card-text">{productdescription}</p>
                                        <p class="card-text"><small class="text-muted">{brand}</small></p>
                                        <p class="card-text"><small class="text-muted">{productprice}</small></p>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
            ))};
            </div>
        );
    }else{
        return(<h3>Error fetching data from server</h3>);
    }
}
export default single_product_list;
